-- =============================================
-- IOP: Life Skills - LUNES (COMPLETO Y √öNICO)
-- =============================================
-- Este script:
-- 1. ELIMINA todas las actividades existentes
-- 2. Inserta SOLO la actividad "Life Skills" con todas sus subactividades y p√°rrafos
-- =============================================

-- PASO 1: Limpiar todas las actividades existentes
DO $$
BEGIN
    -- Primero eliminar referencias en group_schedules
    DELETE FROM group_schedules;
    RAISE NOTICE 'üóëÔ∏è  Todas las referencias en group_schedules eliminadas';
    
    -- Eliminar p√°rrafos de actividades
    DELETE FROM activity_paragraphs;
    RAISE NOTICE 'üóëÔ∏è  Todos los p√°rrafos de actividades eliminados';
    
    -- Eliminar subactividades
    DELETE FROM subactivities;
    RAISE NOTICE 'üóëÔ∏è  Todas las subactividades eliminadas';
    
    -- Eliminar actividades
    DELETE FROM activities;
    RAISE NOTICE 'üóëÔ∏è  Todas las actividades eliminadas';
    
    RAISE NOTICE '‚úÖ Base de datos limpiada completamente';
END $$;

-- PASO 2: Insertar SOLO Life Skills con todas sus subactividades y p√°rrafos
DO $$
DECLARE
    v_activity_id UUID;
    v_decision_making_id UUID;
    v_emotions_id UUID;
    v_daily_routines_id UUID;
    v_housework_id UUID;
    v_family_role_id UUID;
    v_paragraph_count INTEGER;
BEGIN
    -- Crear actividad "Life Skills"
    VALUES ('Life Skills', 'Life Skills activities for IOP groups - Monday', TRUE)
    RETURNING activity_id INTO v_activity_id;
    RAISE NOTICE '‚úÖ Actividad "Life Skills" creada con ID: %', v_activity_id;

    -- =============================================
    -- SUBACTIVIDAD 1: DECISION MAKING (40 p√°rrafos)
    -- =============================================
    VALUES (v_activity_id, 'Decision making', 'Decision making skills and processes', TRUE)
    RETURNING subactivity_id INTO v_decision_making_id;
    RAISE NOTICE '‚úÖ Subactividad "Decision making" creada';


    GET DIAGNOSTICS v_paragraph_count = ROW_COUNT;
    RAISE NOTICE '‚úÖ Insertados % p√°rrafos para "Decision making"', v_paragraph_count;

    -- =============================================
    -- SUBACTIVIDAD 2: MANAGEMENT OF EMOTIONS (8 p√°rrafos)
    -- =============================================
    VALUES (v_activity_id, 'Management of emotions', 'Emotional regulation and management strategies', TRUE)
    RETURNING subactivity_id INTO v_emotions_id;
    RAISE NOTICE '‚úÖ Subactividad "Management of emotions" creada';


    GET DIAGNOSTICS v_paragraph_count = ROW_COUNT;
    RAISE NOTICE '‚úÖ Insertados % p√°rrafos para "Management of emotions"', v_paragraph_count;

    -- =============================================
    -- SUBACTIVIDAD 3: DAILY ROUTINES (40 p√°rrafos)
    -- =============================================
    VALUES (v_activity_id, 'Daily Routines', 'Establishing and maintaining daily routines for stability', TRUE)
    RETURNING subactivity_id INTO v_daily_routines_id;
    RAISE NOTICE '‚úÖ Subactividad "Daily Routines" creada';


    GET DIAGNOSTICS v_paragraph_count = ROW_COUNT;
    RAISE NOTICE '‚úÖ Insertados % p√°rrafos para "Daily Routines"', v_paragraph_count;

    -- =============================================
    -- SUBACTIVIDAD 4: HOUSEWORK MANAGEMENT (44 p√°rrafos)
    -- =============================================
    VALUES (v_activity_id, 'Housework Management', 'Managing household tasks and responsibilities', TRUE)
    RETURNING subactivity_id INTO v_housework_id;
    RAISE NOTICE '‚úÖ Subactividad "Housework Management" creada';


    GET DIAGNOSTICS v_paragraph_count = ROW_COUNT;
    RAISE NOTICE '‚úÖ Insertados % p√°rrafos para "Housework Management"', v_paragraph_count;

    -- =============================================
    -- SUBACTIVIDAD 5: ROLE OF THE FAMILY IN LIFE SKILLS (36 p√°rrafos)
    -- =============================================
    VALUES (v_activity_id, 'Role of the family in life skills', 'Understanding family dynamics and their impact on life skills development', TRUE)
    RETURNING subactivity_id INTO v_family_role_id;
    RAISE NOTICE '‚úÖ Subactividad "Role of the family in life skills" creada';


    GET DIAGNOSTICS v_paragraph_count = ROW_COUNT;
    RAISE NOTICE '‚úÖ Insertados % p√°rrafos para "Role of the family in life skills"', v_paragraph_count;

    RAISE NOTICE '‚úÖ‚úÖ‚úÖ COMPLETADO: Life Skills con todas sus subactividades y p√°rrafos insertados ‚úÖ‚úÖ‚úÖ';

END $$;

-- Verificar que solo existe Life Skills
SELECT 
    a.activity_name,
    s.subactivity_name,
    COUNT(ap.paragraph_id) as total_paragraphs
FROM activities a
LEFT JOIN subactivities s ON s.activity_id = a.activity_id
LEFT JOIN activity_paragraphs ap ON ap.subactivity_id = s.subactivity_id
GROUP BY a.activity_name, s.subactivity_name
ORDER BY a.activity_name, s.subactivity_name;
